document.addEventListener("DOMContentLoaded", function () {
    const dailyOption = document.getElementById('daily-option');
    const monthlyOption = document.getElementById('monthly-option');
    const paymentInfo = document.getElementById('payment-info');
    const detailSubInfoSpan = document.querySelector('.detail-sub__info_span');

    if (!dailyOption || !monthlyOption || !paymentInfo) {
        return;
    }

    dailyOption.addEventListener('change', updatePaymentInfo);
    monthlyOption.addEventListener('change', updatePaymentInfo);

    function updatePaymentInfo() {
        if (dailyOption.checked) {
            detailSubInfoSpan.textContent = '15';
        } else if (monthlyOption.checked) {
            detailSubInfoSpan.textContent = '299';
        }
    }
});


